import React, { Component } from 'react';
import { connect } from 'react-redux';
import CounterControl from '../../components/CounterControl/CounterControl';
import CounterOutput from '../../components/CounterOutput/CounterOutput';
import * as action from '../../store/actions/actions'


class Counter extends Component {
    render() {
        return (
            <div>
                <CounterOutput value={this.props.ctr} />
                <CounterControl label="Increment" clicked={this.props.onIncrementCounter} />
                <CounterControl label="Decrement" clicked={this.props.onDecrementCounter} />
                <CounterControl label="Add 10" clicked={this.props.onAddCounter} />
                <CounterControl label="Subtract 15" clicked={this.props.onSubtractCounter} />
                <hr />
                <button onClick={() => this.props.onStoreResult(this.props.ctr)}>Store Result</button>
                <ul>
                    {
                        this.props.storedResults.map(strResult => (
                            <li key={strResult.id} onClick={() => this.props.onDeleteResult(strResult.id)}>{strResult.value} </li>
                        ))
                    }
                </ul>
            </div>
        );
    }
}
const mapStateToProps = state => {
    return {
        ctr: state.ctr.counter,
        storedResults: state.res.result,
    };
}
const mapDispatchToProps = dispatch => {
    return {
        onIncrementCounter: () => { dispatch(action.increment()) },
        onDecrementCounter: () => { dispatch(action.decrement()) },
        onAddCounter: () => { dispatch(action.add()); },
        onSubtractCounter: () => {
            dispatch({ type: actionType.SUBTRACT, val: 15 })},
        onStoreResult: (result) => dispatch({ type: actionType.STORE_RESULT, result: result }),
        onDeleteResult: (id) => dispatch({ type: actionType.DELETE_RESULT, resultElId: id }),
    };
}
export default connect(mapStateToProps, mapDispatchToProps)(Counter);
