    }
}

export const auth = (email, password, isSignup) => {
    return dispatch => {
        dispatch(authStart());
        const authData = {
            email:email,
            password:password,
            returnSecureToken:true
        }
        let url = 'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyAfoD6o6Yl5KrNha2k7PjpmX8wwMZw6Q9Q';
        if(!isSignup){
            url ='https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyAfoD6o6Yl5KrNha2k7PjpmX8wwMZw6Q9Q'
        }
        axios.post(url, authData)
        .then(response=>{
            console.log(response);
            dispatch(authSuccess(response.data.idToken, response.data.localId));
            dispatch(checkoutTimeout(response.data.expi))
        })
        .catch(err=>{
            console.log(err.response.data.error.message);
            dispatch(authFail(err.response.data.error.message));
        })
    }
}
