import * as actionType from './actions'

const initialState = {
    counter: 0,
    result: [],
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionType.INCREMENT: {
            const newState = Object.assign({}, state);
            newState.counter = state.counter + 1;
            return newState;
        }; break;
        case actionType.ADD: return {
            ...state,
            counter: state.counter + action.val,
        }; break;
        case actionType.SUBTRACT: return {
            ...state,
            counter: state.counter - action.val,
        }; break;
        case 'DECREMENT': return {
            ...state,
            counter: state.counter - 1,
        }; break;
        case 'STORE_RESULT': return {
            ...state,
            result: state.result.concat({ id: new Date(), value: state.counter })
        }; break;
        case 'DELETE_RESULT': {
            // // const id =2
            // const newArray=[...state.result];
            // newArray.splice(id, 1);

            const updatedArray = state.result.filter(result => result.id !== action.resultElId)
            // return updatedArray;
            console.log(action.resultElId);
            return{
                ...state,
                result:updatedArray,
            }

        }
    }
    return state;
};

export default reducer;
