import React, { Component } from 'react';
// import axios from 'axios';
import axios from '../../axios'
import './Blog.css';

class Blog extends Component {
    state = {
        posts: [],
        selectedPostId: null,
        error: false,
    }

    componentDidMount() {
        const posts = axios.get('/posts')
            .then(response => {
                const posts = response.data.slice(0, 4);
                const updatedPosts = posts.map(post => {
                    return {
                        ...post,
                        author: 'Max',
                    };
                });
                this.setState({
                    posts: updatedPosts,
                });
            }).catch((error) => {
                this.setState({ error: true })
                console.log(error);
            });
    }
    postSelectedHandler = (id) => {
        this.setState({ selectedPostId: id })
    }
    render() {
        let posts = <p style={{ textAlign: 'center' }}>Somethings went wrong!</p>
        if (!this.state.error)
            posts = this.state.posts.map(post => {
                return <Post
                    key={post.id}
                    title={post.title}
                    author={post.author}
                    clicked={() => this.postSelectedHandler(post.id)} />;
            });
        return (
            <div className="Blog">
                <header>
                    <nav>
                        <ul>
                            <li><a href="/">Home</a></li>
                            <li><a href="/new-post">New Post</a></li>
                        </ul>
                    </nav>
                </header>

            </div>
        );
    }
}

export default Blog;
